import axios from 'axios'
import React,{useState} from 'react'
const [allHolidays, setAllHolidays] = useState({title:"", start:""  })

export default function Holiday(allHolidays) {
    return (
            axios('https://sholiday.faboul.se/dagar/v2.1/2021')
            .then(response => {
                setAllHolidays(prevAllHolidays=>{
                [...prevAllHolidays ,{title:response.data.dagar.filter(holiday=>holiday.helgdag)}]
                    })
                console.log(allHolidays);
                //   setAllHolidays({title:response.data.dagar[i].helgdag , start:response.data.dagar[i].datum})
                })
     
    )
}
